  styleUrls: ['./disqualifikationskriterien.component.css']
})
export class DisqualifikationskriterienComponent {

  data : any[] = [];
  newItem: any;
  addedItem: any;

  ngOnInit() {
    let url = 'https://api.sheety.co/45604bc63728735dab6c1b62ccf11028/filterkriterienAnbotsabgabe/disqualifikationskriterien';
    fetch(url)
    .then((response) => response.json())
    .then(json => {
