    this.addedItem = Object.assign({}, item);
  }

  onSubmit(form: NgForm) {
    const data = this.addedItem;
    let url = 'https://api.sheety.co/45604bc63728735dab6c1b62ccf11028/filterkriterienAnbotsabgabe/disqualifikationskriterien';
    let body = {
      disqualifikationskriterien: {
        disqualifikationskriterien: data
      }
    }


      //print übergenenen string am ende
      let tempElement = document.createElement('div');
      tempElement.innerHTML = JSON.stringify(body);
      document.body.appendChild(tempElement);
      document.body.append("id:");
      document.body.append(this.addedItem.id);


    fetch(url, {
      method: 'POST',
      body: JSON.stringify(body)
    })
    .then((response) => response.json())
    .then(json => {
      // Do something with object
      console.log(json.disqualifikationskriterien);
    });
  }
}
